import React , { Component } from 'react';
import axios from 'axios';
import "./student.css";
import { Link } from 'react-router-dom';
class SubmissionReview extends Component{

	constructor(){
		super();
		this.state = {
			data : [],
			loading : false
		}	

	}

	componentWillMount(){
		axios.post("http://localhost:5000/faculty/submissionReview",{
			faculty_id : this.props.match.params.id		
		})
		.then(resFromBe => {
			console.log(resFromBe.data.data);
			this.setState({
				data : resFromBe.data.data,
				loading : true			

			})	
		})
	}

	render(){
		let one_question = null;
		if(this.state.data.length !== 0){
			this.state.data.map(once => {
				console.log(once);
				return(
				<Link to = {`/student/ShowProblemStudent/${this.props.match.params.id}/` + once.question_id }><li key = {once.question_id}  className = "question_container"><div className = "question">Que :  {once.question}</div><div className = "difficulty_box">{once.student_id}</div></li></Link>
				)		
			})

		}
		else
		{
		 	one_question = "<h1>No problem is left for review</h1>";		
		}
	return(
	 <React.Fragment>
                {this.state.loading?
                <ul className = "question_list">
                {one_question}
                </ul>:<h1>{null}</h1>}
            </React.Fragment>		
	)	
	}

}


export default SubmissionReview;
