import React , { Component } from 'react';
import axios from 'axios';
import "./faculty.css";
import { Link } from 'react-router-dom';
class SubmissionReview extends Component{

	constructor(){
		super();
		this.state = {
			data : [],
			loading : false
		}	

	}

	componentWillMount(){
		axios.post("http://localhost:5000/faculty/submissionReview",{
			faculty_id : this.props.match.params.id		
		})
		.then(resFromBe => {
			this.setState({
				data : resFromBe.data.data,
				loading : true			

			})	
		})
	}

	render(){
		let one_question = null;
		if(this.state.data.length !== 0){
			one_question  = this.state.data.map(once => {
				return(
				<li>
				<Link to = {"/faculty/ReviewQuestions/" + this.props.match.params.id + "/" + once.question_id}>
					<div className = "list-question">Que : {once.question}</div>
					<div className = "list-submitted-by">submitted by : {once.student_id}</div>
				</Link>	
				</li>				
				)		
			})

		}
		else
		{
		 	one_question = "<h1>No problem is left for review</h1>";		
		}
	return(
	 <React.Fragment>
                {this.state.loading?
                <ul className = "question_list">
                {one_question}
                </ul>:<h1>{null}</h1>}
            </React.Fragment>		
	)	
	}

}


export default SubmissionReview;
