import axios from "axios";
import { Link } from 'react-router-dom';

class MySubmissionStudent extends Component{

	constructor(){
		super();
		this.state = {
			data : [],
			loading : false
		}	
	}

	componentWillMount(){
		axios.post("http://localhost:5000/students/showSubmittedProblems" , {
			student_id : this.props.match.params.id				
		})
		.then(resFromBe => {
			this.setState({
				data : resFromBe.data.data,
				loading : true 
			})	
		})

	}


    render(){
	let one_question = null;
	if(this.state.data.length > 0)
